document.addEventListener("DOMContentLoaded",()=>{var e=document.createElement("button");e.classList.add("menu-toggle"),e.innerHTML="â˜°",document.querySelector("nav").prepend(e),e.addEventListener("click",()=>{document.querySelector("nav ul").classList.toggle("show")}),document.querySelectorAll('a[href^="#"]').forEach(e=>{e.addEventListener("click",function(e){e.preventDefault(),document.querySelector(this.getAttribute("href")).scrollIntoView({behavior:"smooth"})})});let t=window.location.pathname.split("/").pop();document.querySelectorAll("nav ul li a").forEach(e=>{e.getAttribute("href")===t&&e.classList.add("active")})});